    snprintf(name, namesize, "zpl_4inch-%ddpi-%s", dpi, type);
    papplLog(system, PAPPL_LOGLEVEL_DEBUG, "auto driver-name='%s'", name);
  }

  done:

  papplDeviceClose(device);
  return;
}


